

CREATE TABLE IF NOT EXISTS migrations (id INT AUTO_INCREMENT PRIMARY KEY not null, migration varchar not null, batch integer not null);
CREATE TABLE IF NOT EXISTS users (id INT AUTO_INCREMENT PRIMARY KEY not null, name varchar not null, email varchar not null, email_verified_at DATETIME, password varchar not null, remember_token varchar, created_at DATETIME, updated_at DATETIME);
CREATE TABLE IF NOT EXISTS password_reset_tokens (email varchar not null, token varchar not null, created_at DATETIME, primary key (email));
CREATE TABLE IF NOT EXISTS sessions (id varchar not null, user_id integer, ip_address varchar, user_agent text, payload text not null, last_activity integer not null, primary key (id));
CREATE TABLE IF NOT EXISTS cache (key varchar not null, value text not null, expiration integer not null, primary key (key));
CREATE TABLE IF NOT EXISTS cache_locks (key varchar not null, owner varchar not null, expiration integer not null, primary key (key));
CREATE TABLE IF NOT EXISTS jobs (id INT AUTO_INCREMENT PRIMARY KEY not null, queue varchar not null, payload text not null, attempts integer not null, reserved_at integer, available_at integer not null, created_at integer not null);
CREATE TABLE IF NOT EXISTS job_batches (id varchar not null, name varchar not null, total_jobs integer not null, pending_jobs integer not null, failed_jobs integer not null, failed_job_ids text not null, options text, cancelled_at integer, created_at integer not null, finished_at integer, primary key (id));
CREATE TABLE IF NOT EXISTS failed_jobs (id INT AUTO_INCREMENT PRIMARY KEY not null, uuid varchar not null, connection text not null, queue text not null, payload text not null, exception text not null, failed_at DATETIME not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS students (id INT AUTO_INCREMENT PRIMARY KEY not null, telegram_id varchar, name varchar, username varchar, phone_number varchar, is_registered tinyint(1) not null default '0', source varchar, referred_by varchar, is_deleted tinyint(1) not null default '0', created_at DATETIME, updated_at DATETIME);
CREATE TABLE IF NOT EXISTS affiliates (id INT AUTO_INCREMENT PRIMARY KEY not null, name varchar, student_id integer not null, total_referrals integer, converted_referrals integer, total_earnings DECIMAL(10,2), withdrawn_earnings DECIMAL(10,2), remaining_balance DECIMAL(10,2), referral_code varchar, is_active tinyint(1) not null default '0', created_at DATETIME, updated_at DATETIME, foreign key(student_id) references students(id) on delete cascade);
CREATE TABLE IF NOT EXISTS campaigns (id INT AUTO_INCREMENT PRIMARY KEY not null, name varchar, link varchar, referred_count integer, converted_count integer, cost DECIMAL(10,2), created_at DATETIME, updated_at DATETIME);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX users_email_unique on users (email);
CREATE INDEX sessions_user_id_index on sessions (user_id);
CREATE INDEX sessions_last_activity_index on sessions (last_activity);
CREATE INDEX jobs_queue_index on jobs (queue);
CREATE UNIQUE INDEX failed_jobs_uuid_unique on failed_jobs (uuid);
CREATE UNIQUE INDEX students_telegram_id_unique on students (telegram_id);

